mapper.select:
select

from feature_file
where class_num = #{classNum,jdbcType=INTEGER} 
and package_deep = #{packageDeep,jdbcType=INTEGER} 
and package_num = #{packageNum,jdbcType=INTEGER}

mapper.insert:

insert into feature_file (md5, `name`, version, 
  class_num, package_deep, package_num
  )
values (#{md5,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{version,jdbcType=VARCHAR}, 
  #{classNum,jdbcType=INTEGER}, #{packageDeep,jdbcType=INTEGER}, #{packageNum,jdbcType=INTEGER}
  )

mapper.update:

update feature_file



      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.name,jdbcType=VARCHAR}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.version,jdbcType=VARCHAR}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.classNum,jdbcType=INTEGER}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.packageDeep,jdbcType=INTEGER}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.packageNum,jdbcType=INTEGER}



where md5 in

  #{item.md5,jdbcType=VARCHAR}


