mapper.select:
select 

from feature_method
where modifier = #{modifier,jdbcType=INTEGER}
and args_num = #{argsNum,jdbcType=INTEGER}
and return_type = #{returnType,jdbcType=INTEGER}
and cfg_finger = #{cfgFinger,jdbcType=VARCHAR}

select class_md5 from relation_class_method
where method_md5 in (
  select md5
  from feature_method
  where modifier = #{modifier,jdbcType=INTEGER}
  and args_num = #{argsNum,jdbcType=INTEGER}
  and return_type = #{returnType,jdbcType=INTEGER}
  and cfg_finger = #{cfgFinger,jdbcType=VARCHAR}
)

mapper.update:
update feature_method
set modifier = #{modifier,jdbcType=INTEGER},
  args_num = #{argsNum,jdbcType=INTEGER},
  return_type = #{returnType,jdbcType=INTEGER},
  cfg_finger = #{cfgFinger,jdbcType=VARCHAR}
where md5 = #{md5,jdbcType=VARCHAR}

update feature_method



      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.modifier,jdbcType=INTEGER}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.argsNum,jdbcType=INTEGER}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.returnType,jdbcType=INTEGER}




      when md5 = #{item.md5,jdbcType=VARCHAR} then #{item.cfgFinger,jdbcType=VARCHAR}



where md5 in

  #{item.md5,jdbcType=VARCHAR}

